#include <gtest/gtest.h>

#include <queue>
#include <set>
#include <utility>
#include <vector>

//! @struct State
//! @brief State in maze consists of (row, col) location and number of steps
struct State
{
    int row {};
    int col {};
    int steps {};

    State(int row_in, int col_in, int steps_in)
        : row {row_in}
        , col {col_in}
        , steps {steps_in}
    {
    }
};

//! @brief Helper function to determine if maze location is valid (not a wall)
//! @param[in] row  Row of current location
//! @param[in] col  Column of current location
//! @param[in] m    Number of rows in maze
//! @param[in] n    Number of columns in maze
//! @param[in] maze Reference to m x n maze
//! @return True if 
static bool validFA(int                                   row,
                    int                                   col,
                    int                                   m,
                    int                                   n,
                    const std::vector<std::vector<char>>& maze)
{
    return row >= 0
        && row < m
        && col >= 0
        && col < n
        && maze.at(row).at(col) == '.';
}

//! @brief First attempt solution to get shortest path from entrance to exit
//! @param[in] maze     Reference to m x n maze, empty cells = '.', walls = '+'
//! @param[in] entrance Vector containing initial [row, col] location
//! @return Number of steps in the shortest path from entrance to nearest exit
static int nearestExitFA(const std::vector<std::vector<char>>& maze,
                         const std::vector<int>&               entrance)
{
    //! @details Does not pass time limit exceeded test. 156/194 test cases passed.

    const auto m = static_cast<int>(maze.size());
    const auto n = static_cast<int>(maze.front().size());

    const int init_row {entrance.front()};
    const int init_col {entrance.back()};

    /*
    std::set<std::pair<int, int>> seen {};
    seen.emplace(init_row, init_col);
    */

    //! Use 2D seen matrix instead of std::set to avoid time limit exceeded
    std::vector<std::vector<bool>> seen(
        maze.size(), std::vector<bool>(maze.front().size(), false));
    seen[init_row][init_col] = true;

    static const std::vector<std::pair<int, int>> directions {
        {-1, 0}, {0, -1}, {1, 0}, {0, 1}};
    
    std::queue<State> queue {};
    queue.emplace(init_row, init_col, 0);

    while (not queue.empty())
    {
        const auto curr_state = queue.front();
        queue.pop();

        const int row {curr_state.row};
        const int col {curr_state.col};
        const int steps {curr_state.steps};

        // if (seen.count({row, col}) == 0
        if (not seen[row][col]
            && (row == 0 || row == m - 1 || col == 0 || col == n - 1))
        {
            return steps;
        }

        // seen.emplace(row, col);
        seen[row][col] = true;

        for (const auto [dcol, drow] : directions)
        {
            const int nextRow {row + drow};
            const int nextCol {col + dcol};

            if (validFA(nextRow, nextCol, m, n, maze)
                && not seen[nextRow][nextCol])
            //  && seen.count({nextRow, nextCol}) == 0)
            {
                queue.emplace(nextRow, nextCol, steps + 1);
            }
        }
    }

    return -1;

} // static int nearestExitFA( ...

TEST(NearestExitTest, SampleTest1)
{
    const std::vector<std::vector<char>> maze {
        {'+', '+', '.', '+'},
        {'.', '.', '.', '+'},
        {'+', '+', '+', '.'}};
    
    const std::vector<int> entrance {1, 2};

    EXPECT_EQ(1, nearestExitFA(maze, entrance));
}

TEST(NearestExitTest, SampleTest2)
{
    const std::vector<std::vector<char>> maze {
        {'+', '+', '+'},
        {'.', '.', '.'},
        {'+', '+', '+'}};
    
    const std::vector<int> entrance {1, 0};

    EXPECT_EQ(2, nearestExitFA(maze, entrance));
}

TEST(NearestExitTest, SampleTest3)
{
    const std::vector<std::vector<char>> maze {{'.', '+'}};

    const std::vector<int> entrance {0, 0};

    EXPECT_EQ(-1, nearestExitFA(maze, entrance));
}

